import UIKit

protocol CitiesBusinessLogic
{
    func fetchCities(request: Cities.FetchCities.Request)
}

protocol CitiesDataStore
{
    var selectedCity: Cities.City? { get set }
    var cities: [Cities.City] { get set }
}

class CitiesInteractor: CitiesBusinessLogic, CitiesDataStore
{
    var selectedCity: Cities.City?
    var cities: [Cities.City] = []
    
    var presenter: CitiesPresentationLogic?
    var worker: CitiesWorker?
    //var name: String = ""
    
    // MARK: Do something
    
    func fetchCities(request: Cities.FetchCities.Request)
    {
        worker = CitiesWorker()
        let cities = worker?.fetchCities() ?? []
        
        self.cities = cities
        
        let response = Cities.FetchCities.Response(cities: cities)
        presenter?.presentCities(response: response)
    }
}
